mapper.select:
select content_id, content_type_id, title, addr1, addr2, zipcode, tel, first_image ,first_image2, sido_code, gugun_code, latitude, longitude, overview from attraction_info where (title like CONCAT('%', #{keyword}, '%') or addr1 like CONCAT('%', #{keyword}, '%'))
and sido_code = #{sidoCode}
and gugun_code = #{gugunCode}

select content_id, content_type_id, title, addr1, 
		addr2, zipcode, tel, first_image ,first_image2,
		sido_code, gugun_code, latitude, longitude, overview
from attraction_info


		and sido_code = #{sidoCode}


		and gugun_code = #{gugunCode}


		and content_type_id = #{contentTypeId}

select sido_code as sidoCode, gugun_code as gugunCode, gugun_name as gugunName
from gugun
where sido_code = #{sidoCode}
