mapper.select:
select count(*)
from articles
where category_id=#{categoryId}

		select category_id, article_id, article_title, member_name, view_count,
        like_count, register_time, update_time, (SELECT COALESCE(COUNT(*), 0)
        FROM comments
        WHERE article_id = articles.article_id
        ) AS comment_count
		from articles
		where category_id=#{categoryId}



		order by article_id
		limit #{bean.start}, #{bean.interval}

mapper.update:
update articles set
article_title=#{articleTitle}, article_content=#{articleContent},
member_name=#{memberName}, update_time=CURRENT_TIMESTAMP,
category_id=#{categoryId}, image = #{image}
where article_id =
#{articleId}

update articles set
like_count = like_count + 1
where article_id = #{articleId}

update comments set
content = #{content}
where comment_id = #{commentId}
